import java.util.ArrayList;
import java.util.List;

public class Main {

    public static void main(String... args) {
        Solution sol = new Solution();
        System.out.println(Integer.toBinaryString(1 << 4));
        System.out.println(sol.hasAllCodes("00110110", 2));           //true
        System.out.println(sol.hasAllCodes("0110", 1));               //true
        System.out.println(sol.hasAllCodes("00110", 2));              //true
        System.out.println(sol.hasAllCodes("01101", 2));              //false
        System.out.println(sol.hasAllCodes("0110", 2));               //false

        System.out.println(sol.hasAllCodes("00000000010011101", 4));  //false

        System.out.println(sol.hasAllCodes("",
                7));                                                     //false
    }
}
